mapper.insert:
	INSERT INTO schedule
	(sch_id, sch_title, sch_start, sch_end, sch_type, sch_pname, sch_backgroundColor, sch_textColor, sch_allDay, pet_num
	
	
	,sch_description
	
	
	
		
		,sch_walk
		
		
		,sch_expenditure, sch_exDetails
		
		
		,sch_weight
		
	
	
	)
	VALUES
	(seq_sch.NEXTVAL, #{sch_title}, #{sch_start}, #{sch_end}, #{sch_type}, #{sch_pname}, #{sch_backgroundColor}, #{sch_textColor}, #{sch_allDay}, (select  pet_num from pet where mem_num = #{mem_num} and pet_name = #{sch_pname}) 
	
	
	,#{sch_description}
	
	
	
		
		,#{sch_walk}
		
		
		,#{sch_expenditure}, #{sch_exDetails}
		
		
		,#{sch_weight}
		
	
	
	)

mapper.update:
UPDATE schedule SET sch_title = #{sch_title}, sch_start = #{sch_start}, sch_end = #{sch_end}, sch_type = #{sch_type}, sch_pname = #{sch_pname}, sch_backgroundColor = #{sch_backgroundColor}, sch_allDay = #{sch_allDay}, pet_num = (select pet_num from pet where mem_num = #{mem_num} and pet_name = #{sch_pname})
,sch_description = #{sch_description}
WHERE sch_id = #{sch_id}

UPDATE schedule
SET
sch_start = #{sch_start},
sch_end = #{sch_end}     
WHERE
sch_id = #{sch_id}
