mapper.insert:
	INSERT INTO schedule
	(sch_id, sch_title, sch_start, sch_end, sch_type, sch_pname, sch_backgroundColor, sch_textColor, sch_allDay
	
	
	,sch_description
	
	
	)
	VALUES
	(seq_sch.NEXTVAL, #{sch_title}, #{sch_start}, #{sch_end}, #{sch_type}, #{sch_pname}, #{sch_backgroundColor}, #{sch_textColor}, #{sch_allDay} 
	
	
	,#{sch_description}
	
	
	)

mapper.update:
UPDATE schedule
SET
sch_start = #{sch_start},
sch_end = #{sch_end}     
WHERE
sch_id = #{sch_id}
