mapper.select:
select pet_num, pet_name, pet_dogcat, pet_kind, to_char(pet_birth, 'YYYY-MM-DD') pet_birth, mem_num, pet_img
from pet
where mem_num = #{mem_num}

mapper.insert:
INSERT INTO pet
(pet_num, pet_name, pet_dogcat, mem_num

 ,pet_img 


 ,pet_birth 


 ,pet_kind

 )
 VALUES
 (seq_pet.NEXTVAL, #{pet_name}, #{pet_dogcat}, #{mem_num}
 
 ,#{pet_img} 


 ,#{pet_birth} 


 ,#{pet_kind}

 )
